import react from 'react';
import { IPost } from '../../types/index'
import styles from './LargePost.module.css';
import Controls from '../Controls/Controls';

const LargePost = (props: IPost) => {
  return (
  <div className={styles.post} >
    <div className={styles.container}>
      <div className={styles.post__content}>
        <div className={styles.post__date}>{props.date}</div>
        <div className={styles.post__title}>{props.title}</div>
        <div className={styles.post__text}>{props.text}</div>
      </div>
      <div className={styles.img}>
        <img src={props.image} alt='Картинка' />
      </div>
    </div>
    <Controls/>
  </div>
  )
};

export default LargePost;
