import react from 'react';
import styles from './Posts.module.css';
import { PostsProps } from '../../types/index'
import LargePost from '../../components/LargePost/LargePost';
import MediumPost from '../../components/MediumPost/MediumPost';
import SmallPost from '../../components/SmallPost/SmallPost';

const Posts = ({ posts }: PostsProps) => {
let count = 0;
  return (
    <div className={styles.posts}>
      <LargePost id={posts[0].id} image={posts[0].image} text={posts[0].text} date={posts[0].date} lesson_num={posts[0].lesson_num} title={posts[0].title} description={posts[0].description} author={posts[0].author}/>
      {posts.map(post => (count++ + 1 == post.id) ? <SmallPost id={post.id} image={post.image} text={post.text} date={post.date} lesson_num={posts[0].lesson_num} title={posts[0].title} description={posts[0].description} author={posts[0].author} /> : null)}
    </div>
  )
};

export default Posts;
